import './../App.css';
import { useState } from "react"
import arrow_icon from './../Assets/arrow-icon.png'

export default function Input() {
    const [open, setOpen] = useState(false);

    const handleOpen = () => {
        setOpen(!open);
      };

    const [appState, setAppState] = useState('');

    return (
        <>
            <h1 className="Ask">ASK ME ANYTHING</h1>

            <div className="Filter1">
                <input
                    style={{all:"unset"}}
                    type="text"
                    name="state"
                    placeholder="State (Illinois or New Jersey)"
                    value={appState}
                    onChange={e => setAppState(e.target.value)}
                />
                {/* <button style={{all: "unset", cursor:"pointer"}} onClick={handleOpen}>
                    <img src={arrow_icon} className="Arrow" alt="logo" />
                </button> */}
            </div>
            <div className="Filter2">
                <p>Ameren</p>
                <button style={{all: "unset", cursor:"pointer"}} onClick={handleOpen}>
                    <img src={arrow_icon} className="Arrow" alt="logo" />
                </button>
            </div>
            <div className="Question">
                <p>What is the REC value for a 1.2MW commercial rooftop solar project in ComEd?</p>
            </div>
            
            {/* <div>
                <button onClick={handleOpen}>Dropdown</button>
                {open ? (
                    <ul className="menu">
                    <li className="menu-item">
                        <button>Menu 1</button>
                    </li>
                    <li className="menu-item">
                        <button>Menu 2</button>
                    </li>
                    </ul>
                ) : null}
                {open ? <div>Is Open</div> : <div>Is Closed</div>}
            </div> */}
        </>
    )
}
